export class Cell {
    id!: number;
    isAlive: boolean = false;
    siblings: number[] = []

    constructor(id: number) {
        this.id = id;
        this.siblings = this.setSiblings(id);
    }

    setSiblings(id: number): number[] {
        /**
         *  1 | 2 | 3
         *  4 | X | 5
         *  6 | 7 | 8
         */

        const SIMBLINGS: number[] = [
            id - 13, id - 12, id - 11,
            id - 1, id - 2,
            id + 11, id + 12, id + 13,
        ];

        return SIMBLINGS.filter(sibling => sibling > 0 && sibling < 144);
    }
}
