export class Cell {
    id!: number;
    isAlive: boolean = false;
    siblings: number[] = []

    constructor(id: number, max: number) {
        this.id = id;
        this.siblings = this.setSiblings(id, max);
    }

    setSiblings(id: number, max: number): number[] {
        const MAX_SQRT = Math.sqrt(max);
        const DX_LIMIT: number[] = [];
        const SX_LIMIT: number[] = [1];

        for (let i = 1; i <= max; i++) {
            if (i % MAX_SQRT === 0) {
                DX_LIMIT.push(i);
                SX_LIMIT.push(i + 1);
            }
        }

        // console.table([
        //     ["Cell ID", id],
        //     ["DX_LIMIT", DX_LIMIT],
        //     ["SX_LIMIT", SX_LIMIT],
        // ])

        /**
         *  1 | 2 | 3
         *  4 | X | 5
         *  6 | 7 | 8
         */

        const SIMBLINGS: number[] = [
            SX_LIMIT.includes(id) && (id - 13 < 0) ? 0 : id - 13,
            (id - 12 < 0) ? 0 : id - 12,
            (id - 11 < 0) ? 0 : id - 11,
            SX_LIMIT.includes(id) && (id - 1 < 0) ? 0 : id - 1,
            DX_LIMIT.includes(id) && (id + 1 > max) ? 0 : id + 1,
            SX_LIMIT.includes(id) && (id + 11 > max) ? 0 : id + 11,
            (id + 12 > max) ? 0 : id + 12,
            DX_LIMIT.includes(id) && (id + 13 > max) ? 0 : id + 13,
        ];
        /** Ex.:
          *  0   | -11 | -10
          *  0   |  1  | 2
          *  0   |  13 | 14
          */

        return SIMBLINGS.filter(sibling => sibling > 0 && sibling < max);
    }
}
