import { Component, ViewChild } from '@angular/core';
import { GolModalComponent } from '../gol-modal/gol-modal.component';
import { GridService } from 'src/app/services/grid.service';
import { SettingsService } from 'src/app/services/settings.service';
import { Cell } from 'src/app/models/cell.model';

type PatternType = 'heart' | 'cat' | 'tree'
type DirectionType = 'N' | 'NE' | 'E' | 'SE' | 'S' | 'SO' | 'O' | 'NO';

@Component({
  selector: 'gol-pattern',
  templateUrl: './gol-pattern.component.html',
  styleUrls: ['./gol-pattern.component.scss']
})
export class GolPatternComponent {
  @ViewChild(GolModalComponent) modal!: GolModalComponent;

  constructor(
    readonly gridService: GridService,
    private readonly settingsService: SettingsService
  ) { }

  openPattern(pattern: PatternType) {
    console.log("OPEN PATTERN", pattern)
    const ROWS: number = this.settingsService.getRows()
    const CENTER_CELL: Cell = this.gridService.getCell(((ROWS*ROWS)+1)/ 2);
    console.log("ðŸš€ ~ file: gol-pattern.component.ts:27 ~ GolPatternComponent ~ openPattern ~ CENTER_CELL:", CENTER_CELL)
    // let x0y0: number;
    let aliveArray: number[] = [];

    const getIdByDirection = (centralCellId: number, direction: DirectionType): number => {
      switch (direction) {
        case 'NO':
          return this.gridService.getCell(centralCellId).siblings[0];
        case 'N':
          return this.gridService.getCell(centralCellId).siblings[1];
        case 'NE':
          return this.gridService.getCell(centralCellId).siblings[2];
        case 'O':
          return this.gridService.getCell(centralCellId).siblings[3];
        case 'E':
          return this.gridService.getCell(centralCellId).siblings[4];
        case 'SO':
          return this.gridService.getCell(centralCellId).siblings[5];
        case 'S':
          console.log("ðŸš€ ~ file: gol-pattern.component.ts:46 ~ GolPatternComponent ~ getIdByDirection ~ this.gridService.getCell(centralCellId):", this.gridService.getCell(centralCellId))
          return this.gridService.getCell(centralCellId).siblings[6];
        case 'SE':
          return this.gridService.getCell(centralCellId).siblings[7];
      }
    }

    switch (pattern) {
      case 'heart':
        // let x1y1 = this.gridService.getCell(this.gridService.getCell(CENTER_CELL.siblings[5]).siblings[5]).siblings[5];
        console.log("ðŸš€ ~ file: gol-pattern.component.ts:56 ~ GolPatternComponent ~ openPattern ~ CENTER_CELL.id:", CENTER_CELL.id)

        let x4y1 = getIdByDirection(getIdByDirection(getIdByDirection(CENTER_CELL.id, 'S'), 'S'), 'S');
        let x3y2 = getIdByDirection(x4y1, 'NO');
        let x2y3 = getIdByDirection(x3y2, 'NO');
        let x1y4 = getIdByDirection(x2y3, 'NO');
        let x1y5 = getIdByDirection(x1y4, 'N');
        let x1y6 = getIdByDirection(x1y5, 'N');
        let x2y7 = getIdByDirection(x1y6, 'NE');
        let x3y7 = getIdByDirection(x2y7, 'E');
        let x4y6 = getIdByDirection(x3y7, 'SE');
        let x5y7 = getIdByDirection(x4y6, 'NE');
        let x6y7 = getIdByDirection(x5y7, 'E');
        let x7y6 = getIdByDirection(x6y7, 'SE');
        let x7y5 = getIdByDirection(x7y6, 'S');
        let x7y4 = getIdByDirection(x7y5, 'S');
        let x6y3 = getIdByDirection(x7y4, 'SO');
        let x5y2 = getIdByDirection(x6y3, 'SO');

        aliveArray = [x4y1, x3y2, x2y3, x1y4, x1y5, x1y6, x2y7, x3y7, x4y6, x5y7, x6y7, x7y6, x7y5, x7y4, x6y3, x5y2]
        break;
    }

    aliveArray.forEach(coord => {
      this.gridService.getCell(coord).isAlive = true;
    })

    this.modal.close();
  }
}
