import { Component, Input, OnInit } from '@angular/core';
import { Cell } from 'src/app/models/cell.model';
import { GridService } from 'src/app/services/grid.service';

export type CellState = 'alive' | 'death'

@Component({
  selector: 'gol-cell',
  templateUrl: './gol-cell.component.html',
  styleUrls: ['./gol-cell.component.scss']
})
export class GolCellComponent implements OnInit {
  @Input() state: CellState = 'death'
  @Input() cell!: Cell;

  constructor(private readonly gridService: GridService){}

  ngOnInit(): void {
    console.log("ðŸš€ ~ file: gol-cell.component.ts:14 ~ GolCellComponent ~ cell:", this.cell)
  }

  setCellState(){
    this.cell.isAlive = !this.cell.isAlive;
    console.log("ðŸš€ ~ file: gol-cell.component.ts:21 ~ GolCellComponent ~ setCellState ~ this.cell:", this.cell)
    this.cell.siblings.forEach(idSibling => {
      const SIBLING: Cell = this.gridService.getCell(idSibling);
      const IS_SIBLING_ALIVE: boolean = SIBLING.isAlive;
      console.log(`Cell ${idSibling} -> ${IS_SIBLING_ALIVE ? 'Alive' : 'Death'}`)
    })
  }
}
