import { Injectable } from '@angular/core';
import { Cell } from '../models/cell.model';

@Injectable({
  providedIn: 'root'
})
export class GridService {
  private cells: Cell[] = [];

  constructor() { }

  getCells(): Cell[] {
    return this.cells;
  }
  setCells(cells: Cell[]) {
    this.cells = cells;
  }

  getCell(idCell: number): Cell {
    return this.cells[idCell-1];
  }
}
