import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'game-of-life';
}
<div class="container">
    <div class="container--header">
        <h1>Conway's</h1>
        <h2>Game of Life</h2>
    </div>
    
    <div class="container--grid">
        <gol-grid></gol-grid>
    </div>
</div>
